import React, { useReducer } from 'react'
import { Button, Card, CardImg, Col, Container, Row } from 'reactstrap';

const initialState = {
    jumlah: 1,
    hargaSatuan: 100000,
    hargaTotal: 100000
};

const reducer = (state, action) =>{
    switch(action.type) {
        case 'tambah' : return {
            ...state,
            jumlah: state.jumlah + 1,
            hargaTotal: state.hargaSatuan + (state.hargaSatuan * state.jumlah)
        }
        case 'kurang' : return {
            ...state,
            jumlah: state.jumlah - 1,
            hargaTotal: (state.hargaSatuan * state.jumlah) - state.hargaSatuan
        }
        default:
            return state;
    }
}

const HooksReducer = () => {
    const [count, dispatch] = useReducer(reducer, initialState);
    return (
        <Container>
            <br />
            <Row>
                <Col xs="6">
                    <Card>
                        <CardImg top width="100%" src="https://placeimg.com/640/480/tech" alt="Card image cap" />
                    </Card>
                </Col>
                <Col xs="6">
                    <h3>Action Figure Naruto Sasuke Sakura Kakashi Gaara</h3>
                    <p>Harga</p>
                    <h3>Rp. {count.hargaSatuan}</h3>
                    <p>Jumlah</p>
                    <Row>
                        <Col>
                            <Button color='danger' onClick={()=> dispatch({type:'tambah'})}>+</Button>
                        </Col>
                        <Col>{count.jumlah}</Col>
                        <Col>
                            {
                                count.jumlah === 1 ?
                                <Button onClick={()=> dispatch({type: 'kurang'})} disabled>-</Button>  
                                :
                                <Button onClick={()=> dispatch({type: 'kurang'})}>-</Button> 
                            }
                            
                        </Col>
                    </Row>
                    <br />
                    <Button color='success' size='large'>
                        Total Rp. {count.hargaTotal}
                    </Button>
                </Col>
            </Row>
        </Container>
    )
}

export default HooksReducer;
